mapper.select:
SELECT b.id, b.title, b.created_at, u.nm FROM board b JOIN user u ON b.user_id = u.id
WHERE b.title LIKE '%${searchText}%' OR b.contents LIKE '%${searchText}%'
ORDER BY b.id DESC LIMIT #{startIdx}, #{size};

SELECT  CEIL( COUNT(1) / #{size} ) FROM board

    WHERE title LIKE '%${searchText}%'
    OR contents LIKE '%${searchText}%';
